import { useEffect, useMemo, useState } from "react";


import { useWeb3 } from "@3rdweb/hooks"; //import thirdweb
import { ThirdwebSDK, TokenModule } from "@3rdweb/sdk"; //import thirdweb sdk
import { ethers } from "ethers"; //import ethers to interact with the blockchain
import Home from "./components/Home.jsx"; // import Home component


const sdk = new ThirdwebSDK("rinkeby"); // We instantiate the SDK on Rinkeyby
const bundleDropModule = sdk.getBundleDropModule("0x3f1e9Cdd485191b8658aD311e49f77Bc021f0749");  // We can grab a reference to our ERC-1155 contract.
const tokenModule = sdk.getTokenModule("0xA21123e4a201D1227E46536F4A1CA63D89fdd6AA"); // We can grab a reference to our ERC-20 token contract.
const voteModule = sdk.getVoteModule("0xe1c1e8bc1B20AeAf8702C6f87D77422bed61C27f"); // We can grab a reference to our voting contract.

const App = () => {
  const { connectWallet, address, error, provider } = useWeb3();
  console.log("üëã Address:", address);
  const signer = provider ? provider.getSigner() : undefined; // The signer is required to sign transactions on the blockchain. Without it we can only read data, not write.
  const [hasClaimedNFT, setHasClaimedNFT] = useState(false); // hasClaimed lets us easily track whether or not a member has already claimed NFT membership
  const [isClaiming, setIsClaiming] = useState(false);   // isClaiming lets us easily keep a loading state while the NFT is minting.
  const [memberTokenAmounts, setMemberTokenAmounts] = useState({});  // Holds the amount of token each member has in state.
  const [memberAddresses, setMemberAddresses] = useState([]);   // The array holding all of our members addresses.
  const [proposals, setProposals] = useState([]); // The array holding all the proposals
  const [isVoting, setIsVoting] = useState(false); // isVoting lets us easily keep a voting state while the voting is carried out
  const [hasVoted, setHasVoted] = useState(false); // hasVoted lets us easily track whether or not a member has already voted
  const shortenAddress = (str) => { return str.substring(0, 6) + "..." + str.substring(str.length - 4);}; // A fancy function to shorten someones wallet address

  // useEffect #1: Grabs all our the addresses of our members holding our NFT. Is executed whenever the "hasClaimedNFT" value changes
  useEffect(() => {
    if (!hasClaimedNFT) {
      return;
    }

    // Just like we did in the 7-airdrop-token.js file! Grab the users who hold our NFT
    // with tokenId 0.
    bundleDropModule
      .getAllClaimerAddresses("0")
      .then((addresess) => {
        console.log("üöÄ Members addresses", addresess);
        setMemberAddresses(addresess);
      })
      .catch((err) => {
        console.error("failed to get member list", err);
      });
  }, [hasClaimedNFT]);

  // useEffect #2: Grabs the # of token each member holds. Is executed whenever the "hasClaimedNFT" value changes
  useEffect(() => {
    if (!hasClaimedNFT) {
      return;
    }

    // Grab all the balances.
    tokenModule
      .getAllHolderBalances()
      .then((amounts) => {
        console.log("üëú Amounts", amounts);
        setMemberTokenAmounts(amounts);
      })
      .catch((err) => {
        console.error("failed to get token amounts", err);
      });
  }, [hasClaimedNFT]);

  // useMemo: Now, we combine the memberAddresses and memberTokenAmounts into a single array
  const memberList = useMemo(() => {
    return memberAddresses.map((address) => {
      return {
        address,
        tokenAmount: ethers.utils.formatUnits(
          // If the address isn't in memberTokenAmounts, it means they don't
          // hold any of our token.
          memberTokenAmounts[address] || 0,
          18
        ),
      };
    });
  }, [memberAddresses, memberTokenAmounts]);

  // useEffect #3: Ensures  our signer can interact with our deployed contract. Is executed whenever the "signer" value changes
  useEffect(() => {
    // We pass the signer to the sdk, which enables us to interact with
    // our deployed contract!
    sdk.setProviderOrSigner(signer);
  }, [signer]);

  // useEffect #4 Checks if the user has membership NFT. Is executed whenever the "address" value changes
  useEffect(() => {
    if (!address) {
      return;
    }
    return bundleDropModule
      .balanceOf(address, "0")
      .then((balance) => {
        if (balance.gt(0)) {
          setHasClaimedNFT(true);
          console.log("üåü this user has a membership NFT!");
        } else {
          setHasClaimedNFT(false);
          console.log("üò≠ this user doesn't have a membership NFT.");
        }
      })
      .catch((error) => {
        setHasClaimedNFT(false);
        console.error("failed to nft balance", error);
      });
  }, [address]);

  // useEffect #5: Retreives all our existing proposals from the contract. Is executed whenever the "hasClaimedNFT" value flips
  useEffect(() => {
    if (!hasClaimedNFT) {
      return;
    }
    // A simple call to voteModule.getAll() to grab the proposals.
    voteModule
      .getAll()
      .then((proposals) => {
        // Set state!
        setProposals(proposals);
        console.log("üåà Proposals:", proposals);
      })
      .catch((err) => {
        console.error("failed to get proposals", err);
      });
  }, [hasClaimedNFT]);

  // useEffect #6: Checks if the user already voted. Is executed whenever the "hasClaimedNFT", "proposals" or "address" values change
  useEffect(() => {
    if (!hasClaimedNFT) {
      return;
    }

    // If we haven't finished retreieving the proposals from the useEffect above
    // then we can't check if the user voted yet!
    if (!proposals.length) {
      return;
    }

    // Check if the user has already voted on the first proposal.
    voteModule
      .hasVoted(proposals[0].proposalId, address)
      .then((hasVoted) => {
        setHasVoted(hasVoted);
        console.log("ü•µ User has already voted");
      })
      .catch((err) => {
        console.error("failed to check if wallet has voted", err);
      });
  }, [hasClaimedNFT, proposals, address]);


  // After al the use effects taking care of function executions due to state changes, we finally decide what to render. 
  // If it's unsupported chain, inform the user
  if (error && error.name === "UnsupportedChainIdError") {
    return (
      <div className="unsupported-network">
        <h2>Please connect to Rinkeby</h2>
        <p>
          This dapp only works on the Rinkeby network, please switch networks in
          your connected wallet.
        </p>
      </div>
    );
  }

  // If there's no address, show the "connect" wallet Home page
  if (!address) {
    return <Home clickHandler={() => connectWallet("injected")} />;
  }

  // If the user has already claimed their NFT we want to display the interal DAO page to them
  // only DAO members will see this. Render all the members + token amounts.
  if (hasClaimedNFT) {

  }

  // Render mint nft screen.
  return (
    <div className="mint-nft">
      <h1>Mint your free üç™DAO Membership NFT</h1>
      <button
        disabled={isClaiming}
        onClick={() => {
          setIsClaiming(true);
          // Call bundleDropModule.claim("0", 1) to mint nft to user's wallet.
          bundleDropModule
            .claim("0", 1)
            .catch((err) => {
              console.error("failed to claim", err);
              setIsClaiming(false);
            })
            .finally(() => {
              // Stop loading state.
              setIsClaiming(false);
              // Set claim state.
              setHasClaimedNFT(true);
              // Show user their fancy new NFT!
              console.log(
                `Successfully Minted! Check it our on OpenSea: https://testnets.opensea.io/assets/${bundleDropModule.address}/0`
              );
            });
        }}
      >
        {isClaiming ? "Minting..." : "Mint your nft (FREE)"}
      </button>
    </div>
  );
};

export default App;
